mapper.insert:
insert into t_dev (id, code, iCustomerID,
  region, site, name,
  type, ip, ser_num, remark,
  create_time, update_time, status
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=INTEGER}, #{icustomerid,jdbcType=INTEGER},
  #{region,jdbcType=INTEGER}, #{site,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR},
  #{type,jdbcType=INTEGER}, #{ip,jdbcType=VARCHAR}, #{serNum,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR},
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}
  )

mapper.select:
select id,name from t_dev where 1 = 1

    AND customer = #{customerId}
;

select id,name from t_dev_type;

    AND customer = #{customerId}
;

SELECT `d`.id AS `devId`, `d`.status AS `devStatus`, `d`.`name` AS `devName`, `d`.code AS `devCode`, `dt`.id AS `devTypeId`, `dt`.`name` AS `devTypeName`, `d`.ser_num AS `devSerNum`, `r`.id AS `regionId`, `r`.`name` AS `regionName`, `d`.site AS `devSite`, `d`.remark AS `devRemark` FROM (SELECT * from t_dev WHERE 1 = 1
AND t_dev.iCustomerID = #{customerId,jdbcType=INTEGER}
AND t_dev.type = #{devTypeId,jdbcType=INTEGER}
AND t_dev.name LIKE '%' #{devName,jdbcType=VARCHAR} '%'
AND t_dev.id = #{regionId,jdbcType=INTEGER}
)AS `d` LEFT JOIN t_dev_type AS `dt` ON `d`.type = `dt`.id LEFT JOIN t_region AS `r` ON `d`.region = `r`.id

select dev.id,code, iCustomerID, region, site, dev.name, dev.type, ip, ser_num, create_time, update_time, status from t_dev dev LEFT JOIN t_dev_type type ON dev.type = type.id where 1=1
AND iCustomerID = #{icustomerid}
AND type.id = #{type}

SELECT
COUNT(*)
FROM
t_dev
WHERE 1=1
AND iCustomerID = #{icustomerid}
AND status = #{status}
AND type = #{type}
