mapper.select:
SELECT dCollectDT, fDust AS fdust, dev_id AS devid FROM t_dust WHERE 1 = 1
AND dev_id= #{devid,jdbcType=BIGINT}
ORDER BY dCollectDT DESC LIMIT 1

SELECT DATE_FORMAT( dCollectDT, '%Y-%m-%d %H:00:00' ) as dCollectDT, fDust AS fdust, dev_id AS devid FROM t_dust_history WHERE 1 = 1
AND dev_id= #{devid,jdbcType=BIGINT}
AND dCollectDT BETWEEN #{dcollectstart,jdbcType=TIMESTAMP} AND #{dcollectend,jdbcType=TIMESTAMP} group by DATE_FORMAT(dCollectDT,'%Y-%m-%d %H') ORDER BY id ASC LIMIT 24

mapper.update:
update t_dust
set dCollectDT = #{dcollectdt,jdbcType=TIMESTAMP},
  fDust = #{fdust,jdbcType=REAL},
  dev_id = #{devId,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}
