mapper.select:
SELECT DATE_FORMAT( dCollectDT, '%Y-%m-%d %H:00:00' ) as dCollectDT, AVG( gCH4 ) AS gCH4, dev_id AS devid FROM t_gas_history WHERE 1 = 1
AND dev_id= #{devid,jdbcType=BIGINT}
AND dCollectDT BETWEEN #{dcollectstart,jdbcType=TIMESTAMP} AND #{dcollectend,jdbcType=TIMESTAMP} group by DATE_FORMAT(dCollectDT,'%Y-%m-%d %H') ORDER BY id ASC LIMIT 24

SELECT DISTINCT * FROM (SELECT tdt.`name` AS typename, td.`name` AS devname, td.site as site, td.ip AS ip, td.`status` AS `status`, td.update_time as updatetime, tg.gCH4 AS gch4 FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_gas tg ON td.id = tg.dev_id WHERE 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
) a

SELECT DISTINCT * FROM ( SELECT tdt.`name` AS typename, td.`name` AS devname, td.site AS site, td.ip AS ip, td.`status` AS `status`, td.update_time AS updatetime, tc.gCO AS gCO FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_co tc ON td.id = tc.dev_id WHERE 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
) a

SELECT DATE_FORMAT( dCollectDT, '%Y-%m-%d %H:00:00' ) as dCollectDT, AVG( gCO ) AS gCO, dev_id AS devid FROM t_co_history WHERE 1 = 1
AND dev_id= #{devid,jdbcType=BIGINT}
AND dCollectDT BETWEEN #{dcollectstart,jdbcType=TIMESTAMP} AND #{dcollectend,jdbcType=TIMESTAMP} group by DATE_FORMAT(dCollectDT,'%Y-%m-%d %H') ORDER BY id ASC LIMIT 24

SELECT * from t_co_history WHERE 1 = 1
AND dev_id= #{devid,jdbcType=BIGINT}
ORDER BY dCollectDT DESC LIMIT 3

SELECT DISTINCT count(*) FROM (SELECT tdt.`name` AS typename, td.`name` AS devname, td.site as site, td.ip AS ip, td.`status` AS `status`, td.update_time as updatetime, tg.gCH4 AS gch4 FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_gas tg ON td.id = tg.dev_id WHERE 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
) a

SELECT DISTINCT count(*) FROM ( SELECT tdt.`name` AS typename, td.`name` AS devname, td.site AS site, td.ip AS ip, td.`status` AS `status`, td.update_time AS updatetime, tc.gCO AS gCO FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_co tc ON td.id = tc.dev_id WHERE 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
) a

SELECT td.id AS devid, td.type AS type, tdt.`name` AS typename, td.`name` AS devname, td.site AS site, td.ip AS ip, td.code AS code, td.`online` AS `online`, tc.dCollectDT AS updateTime, tc.gCO AS val FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_co tc ON td.id = tc.dev_id where 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
AND td.iCustomerID = #{icustomerid,jdbcType=INTEGER}
UNION SELECT td.id AS devid, td.type AS type, tdt.`name` AS typename, td.`name` AS devname, td.site AS site, td.ip AS ip, td.code AS code, td.`online` AS `online`, tg.dCollectDT AS updateTime, tg.gCH4 AS val FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_gas tg ON td.id = tg.dev_id where 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
AND td.iCustomerID = #{icustomerid,jdbcType=INTEGER}

SELECT count( * ) FROM ( SELECT td.id AS devid, td.type AS type, tdt.`name` AS typename, td.`name` AS devname, td.site AS site, td.ip AS ip, td.`online` AS `online`, tc.dCollectDT AS updateTime, tc.gCO AS val FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_co tc ON td.id = tc.dev_id where 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
AND td.iCustomerID = #{icustomerid,jdbcType=INTEGER}
UNION SELECT td.id AS devid, td.type AS type, tdt.`name` AS typename, td.`name` AS devname, td.site AS site, td.ip AS ip, td.`status` AS `status`, tg.dCollectDT AS updateTime, tg.gCH4 AS val FROM t_dev td INNER JOIN t_dev_type tdt ON td.type = tdt.id INNER JOIN t_gas tg ON td.id = tg.dev_id where 1=1 AND td.`online` = #{online,jdbcType=INTEGER}
AND td.`name` LIKE "%"#{devname,jdbcType=VARCHAR}"%"
AND td.type = #{type,jdbcType=INTEGER}
AND td.iCustomerID = #{icustomerid,jdbcType=INTEGER}
) a

mapper.update:
update t_gas
set dcollectdt = #{dcollectdt,jdbcType=TIMESTAMP},
  gCH4 = #{gch4,jdbcType=REAL},
  dev_id = #{devId,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}
