mapper.select:
SELECT tu.cUserName AS username, top.operationDate AS operationdate, top.tUrlName AS turlname, top.`status` AS `status` FROM t_operation_log top INNER JOIN t_user tu ON tu.iUserID = top.operationUserID WHERE 1 = 1
AND top.operationDate BETWEEN #{dcollectstart,jdbcType=TIMESTAMP}
AND #{dcollectend,jdbcType=TIMESTAMP}
AND tu.cUserName like #{username,jdbcType=VARCHAR}

SELECT
count(*)
FROM
t_operation_log top
INNER JOIN t_user tu ON tu.iUserID = top.operationUserID
WHERE
1 = 1

    AND top.operationDate BETWEEN #{dcollectstart,jdbcType=TIMESTAMP}


    AND #{dcollectend,jdbcType=TIMESTAMP}

AND tu.cUserName like #{username,jdbcType=VARCHAR}
