mapper.select:
SELECT dCollectDT,tpressure,dev_id as devid FROM t_pressure WHERE 1 = 1
AND dev_id= #{pressureid,jdbcType=BIGINT}
ORDER BY dCollectDT DESC LIMIT 1

SELECT DATE_FORMAT( dCollectDT, '%Y-%m-%d %H:00:00' ) as dCollectDT, avg(tpressure) as tpressure FROM t_pressure_history WHERE 1 = 1
AND dev_id= #{devid,jdbcType=BIGINT}
AND dCollectDT BETWEEN #{dcollectstart,jdbcType=TIMESTAMP} AND #{dcollectend,jdbcType=TIMESTAMP} group by DATE_FORMAT(dCollectDT,'%Y-%m-%d %H') ORDER BY id ASC LIMIT 24
