mapper.select:
SELECT tu.id as id, tc.iCustomerID as icustomerid, tu.cUserName as username , td .iDeptID as ideptid, tr.ipopedom as ipopedom, tu.mobile as phone, tr.cDspName as userrole, tc.cCustomerName as customername, td.DeptName as department FROM t_user tu LEFT JOIN t_userrole tr ON tu.iPopedom = tr.iPopedom LEFT JOIN t_customer tc ON tu.iCustomerID = tc.iCustomerID LEFT JOIN t_dept td ON td.iDeptID = tu.iDeptID WHERE 1=1
and tu.cUserName like #{cusername,jdbcType=VARCHAR}
AND td.iCustomerID=#{icustomerid,jdbcType=INTEGER}

SELECT * FROM t_user WHERE 1=1
and cUserName=#{cusername,jdbcType=VARCHAR}
AND td.iCustomerID=#{icustomerid,jdbcType=INTEGER}

SELECT count( * ) FROM t_user tu LEFT JOIN t_userrole tr ON tu.iPopedom = tr.iPopedom LEFT JOIN t_customer tc ON tu.iCustomerID = tc.iCustomerID LEFT JOIN t_dept td ON td.iDeptID = tu.iDeptID WHERE 1 = 1
and tu.cUserName like #{cusername,jdbcType=VARCHAR}
AND td.iCustomerID=#{icustomerid,jdbcType=INTEGER}
